define(['jquery', 'mage/storage', 'Magento_Ui/js/model/messageList', 'Magento_Customer/js/customer-data', 'mage/translate'], function($, storage, globalMessageList, customerData, $t) {
    'use strict';
    var callbacks = [],
        action = function(loginData, redirectUrl, isGlobal, messageContainer) {
            messageContainer = messageContainer || globalMessageList;
            return storage.post('customer/ajax/login', JSON.stringify(loginData), isGlobal).done(function(response) {
                if (response.errors) {
                    messageContainer.addErrorMessage(response);
                    callbacks.forEach(function(callback) {
                        callback(loginData);
                    });
                } else {
                    callbacks.forEach(function(callback) {
                        callback(loginData);
                    });
                    customerData.invalidate(['customer']);
                    if (response.redirectUrl) {
                        if (loginData['g-recaptcha-response'] != '') {
                            window.location.href = response.redirectUrl;
                        } else {
                            return 0;
                        }
                    } else if (redirectUrl) {
                        if (loginData['g-recaptcha-response'] != '') {
                            window.location.href = redirectUrl;
                        } else {
                            return 0;
                        }
                    } else {
                        if (loginData['g-recaptcha-response'] != '') {
                            location.reload();
                        } else {
                            return 0;
                        }
                    }
                }
            }).fail(function() {
                messageContainer.addErrorMessage({
                    'message': $t('Could not authenticate. Please try again later test')
                });
                callbacks.forEach(function(callback) {
                    callback(loginData);
                });
            });
        };
    action.registerLoginCallback = function(callback) {
        callbacks.push(callback);
    };
    return action;
});